$(document).ready(function () {
    function getMessage() {
        // clean the inbox first
        $('#message-list').empty();
        $.ajax({
            url: "/admin/message/get",
            method: "GET",
            success: function (data) {
                $messages = data;
                $count = $messages.length;
                const messageTemplate = $('.messageTemplate');
                $('.message-count').addClass('d-none');
                $.each($messages, function (index, item) {
                    $('.message-count').removeClass('d-none');
                    $('.message-count').text($count);
                    let messageItem = messageTemplate.clone().removeClass('d-none');
                    messageItem.find('#message-index').text(index+1);
                    messageItem.find('#message-subject').text(item.message);
                    messageItem.find('#message-info').text(convertDateTime(item.created_at) + ' · '+ getTimePassedBy(item.created_at));
                    messageItem.find('#message-link').attr('href', '/admin/message/' + item.id);
                    $('#message-list').append(messageItem);
                });
            }
        })
    }
    function getPendingBill(){
        $('#pending-bill-list').empty();
        $.ajax({
            url: "/admin/bill/get-pending",
            method: "GET",
            success: function (data) {
                console.log(data);
                $bills = data;
                $count = data.length;
                console.log('bill length',$count);
                $('.pending-bill-count').addClass('d-none');
                const billTemplate = $('.billPendingTemplate');
                $.each($bills, function (index, item) {
                    $('.pending-bill-count').text(index);
                    $('.pending-bill-count').removeClass('d-none');
                    let billItem = billTemplate.clone().removeClass('d-none');
                    billItem.find('#bill-description').text('#'+item.id + ' · '+ 'Đơn hàng chưa thanh toán');
                    billItem.find('#bill-index').text(index);
                    billItem.find('#bill-phone').text(item.full_name+ ' · '+item.phone_number);
                    billItem.find('#order_date').text('Vào ' + convertDateTime(item.order_date) + ' · '+ getTimePassedBy(item.order_date));
                    billItem.find('#bill-link').attr('href', '/admin/bill/' + item.id);
                    $('#pending-bill-list').append(billItem);
                });
            }
        })
    }
    getMessage();
    getPendingBill();
    setInterval(function () {
        getPendingBill();
    }, 30000);
});

function getTimePassedBy(createTime) {
    const now = new Date();
    const elapsedTime = now - new Date(createTime);
    const elapsedMinutes = Math.floor(elapsedTime / 60000);

    if (elapsedMinutes < 1) {
        return 'vừa xong';
    } else if (elapsedMinutes < 60) {
        return elapsedMinutes + ' phút trước';
    } else if (elapsedMinutes < 1440) {
        const elapsedHours = Math.floor(elapsedMinutes / 60);
        return elapsedHours + ' giờ trước';
    } else {
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(createTime).toLocaleDateString('vi-VN', options);
    }
}

function convertDateTime(datetime) {
    const dateObj = new Date(datetime);
    const day = String(dateObj.getDate()).padStart(2, '0');
    const month = String(dateObj.getMonth() + 1).padStart(2, '0');
    const hours = String(dateObj.getHours()).padStart(2, '0');
    const minutes = String(dateObj.getMinutes()).padStart(2, '0');
    const formattedDatetime = `${day}/${month} lúc ${hours}:${minutes}`;
    return formattedDatetime;
  }



function generateVoucherCode()
{
    var result = generateCode();
    $('#voucherCodeField').val(result);
    console.log(result);
}

function generateCode() {
    let result = '';
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    for (let i = 0; i < 10; i++) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result.toUpperCase();
}

